function func2()
{
	var pr1_t = parseInt(GetObjectiveScore("pr1"));
	var pr2_t = parseInt(GetObjectiveScore("pr2"));
	var pr3_t = parseInt(GetObjectiveScore("pr3"));
	var pr4_t = parseInt(GetObjectiveScore("pr4"));
	var pr5_t = parseInt(GetObjectiveScore("pr5"));
	var pr6_t = parseInt(GetObjectiveScore("pr6"));
	var pr7_t = parseInt(GetObjectiveScore("pr7"));
	var qu1_t = parseInt(GetObjectiveScore("qu1"));
	var qu2_t = parseInt(GetObjectiveScore("qu2"));
	var qu3_t = parseInt(GetObjectiveScore("qu3"));
	var qu4_t = parseInt(GetObjectiveScore("qu4"));
	var qu5_t = parseInt(GetObjectiveScore("qu5"));
	var qu6_t = parseInt(GetObjectiveScore("qu6"));
	var qu7_t = parseInt(GetObjectiveScore("qu7"));

	var sScore_t = Math.round((pr1_t+pr2_t+pr3_t+pr4_t+pr5_t+pr6_t+pr7_t+qu1_t+qu2_t+qu3_t+qu4_t+qu5_t+qu6_t+qu7_t)*100/14);

	SetObjectiveScore("total", "", "replace", sScore_t);

	if ((pr1_t+pr2_t+pr3_t+pr4_t+pr5_t+pr6_t+pr7_t+qu1_t+qu2_t+qu3_t+qu4_t+qu5_t+qu6_t+qu7_t)>=11)
	{
		setSlideCountInFrameCounter('FRAME_172');
	}
	else processEvent('next_1');
}
	
func1();

