	$('#contents_window').show();
	if (!g_isMSIE) {
		$("#contents_cust").mCustomScrollbar({
			theme:"light",
			scrollInertia: 400,
			autoDraggerLength: false
		});
	}
	else {
		document.getElementById('contents_cust').style.width="850px";
	}

}
else {
$("#contents_cust").mCustomScrollbar("destroy");
	$('#contents_window').hide();
