var chapter = $('#position_c_position_current').text();

$('.p_contents_line > span').css('cursor', 'pointer');

var a_score = [];
for (i=1;i<=59;i++)
{
	if (i<=9){
		a_score[i]=GetObjectiveScore('c0' + i);
	}
	else {
		a_score[i]=GetObjectiveScore('c'+ i);
	}
}

for (i=1;i<=59;i++)
{
	if (a_score[i]==1){
		if (i<=9)
		{
			$('#c0'+i+' > .p_contents_num').removeClass('p_cn_c');
			$('#c0'+i+' > .p_contents_line').removeClass('p_cl_c');
			$('#c0'+i+' > .p_contents_num').addClass('p_cn_v');
			$('#c0'+i+' > .p_contents_line').addClass('p_cl_v');
		}
		else 
		{
			$('#c'+i+' > .p_contents_num').removeClass('p_cn_c');
			$('#c'+i+' > .p_contents_line').removeClass('p_cl_c');
			$('#c'+i+' > .p_contents_num').addClass('p_cn_v');
			$('#c'+i+' > .p_contents_line').addClass('p_cl_v');
		}
	}
	else if (a_score[i]==0){
		if (i<=9)
		{
			$('#c0'+i+' > .p_contents_num').removeClass('p_cn_c');
			$('#c0'+i+' > .p_contents_line').removeClass('p_cl_c');
			$('#c0'+i+' > .p_contents_num').removeClass('p_cn_v');
			$('#c0'+i+' > .p_contents_line').removeClass('p_cl_v');
		}
		else 
		{
			$('#c'+i+' > .p_contents_num').removeClass('p_cn_c');
			$('#c'+i+' > .p_contents_line').removeClass('p_cl_c');
			$('#c'+i+' > .p_contents_num').removeClass('p_cn_v');
			$('#c'+i+' > .p_contents_line').removeClass('p_cl_v');
		}
	}
}
for (i=1;i<=59;i++)
{
	if (chapter==i)
	{
		if (i<=9)
		{
			$('#c0'+i+' > .p_contents_num').removeClass('p_cn_v');
			$('#c0'+i+' > .p_contents_line').removeClass('p_cl_v');
			$('#c0'+i+' > .p_contents_num').addClass('p_cn_c');
			$('#c0'+i+' > .p_contents_line').addClass('p_cl_c');
		}
		else 
		{
			$('#c'+i+' > .p_contents_num').removeClass('p_cn_v');
			$('#c'+i+' > .p_contents_line').removeClass('p_cl_v');
			$('#c'+i+' > .p_contents_num').addClass('p_cn_c');
			$('#c'+i+' > .p_contents_line').addClass('p_cl_c');
		}
	}
}

methods.method:
function Constructor(argobj)
				{
					AddEvtHandler("EVENT_SLIDE_OPENED", "nav_010_slidename", argobj.pid );
					return true;
				}

function HandleEvt(oEvtHandler)
				{
					if(document.getElementById(oEvtHandler.sTargetId)==null) return false;
					switch (oEvtHandler.sEvt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							document.getElementById(oEvtHandler.sTargetId + "_SlideName").innerHTML = g_oSlide!=null ? g_oSlide.getAttribute("name") : "";
							break;
						}
					}
					return true;
				}

function GetProperty(argobj)
				{
					if(argobj.pid==null || argobj.property==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					switch(argobj.property.toLowerCase())
					{
						case "slidename":	g_vPropertyValue = g_oSlide!=null ? g_oSlide.getAttribute("name") : "";	break;
						default: return true;
					}
					return true;
				}

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
				if(oParams.selectSingleNode("n1_img").text=="" || oParams.selectSingleNode("n2_img").text=="" || oParams.selectSingleNode("n3_img").text=="") return false;
				var bNaviButton = (oParams.selectSingleNode("navibutton").text=="yes");
				var bNavLock = (oParams.selectSingleNode("nav_lock").text=="yes");
				var bUseMethod = (oParams.selectSingleNode("use_object").text=="yes");
				oCurObj.setAttribute("state","enabled");
				var oEventDiv = oCurObj.getElementsByTagName("div")[0]; 
				if(oEventDiv.getAttribute("onclick")!=null && oEventDiv.getAttribute("body_click")==null)
				{
					var sBody = oEventDiv.getAttribute("onclick").toString();
					sBody = sBody.substr(sBody.indexOf("{")+1);
					if(sBody.indexOf("return")!=-1)
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("return"));
					}
					else
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("}"));
					}
					oEventDiv.setAttribute("body_click", sBody);
					oEventDiv.onclick = function ()	{ if(this.parentNode.getAttribute('state')=='enabled') eval(this.getAttribute("body_click")); };
				}
				if(oEventDiv.getAttribute("ondblclick")!=null && oEventDiv.getAttribute("body_dblclick")==null)
				{
					var sBody = oEventDiv.getAttribute("ondblclick").toString();
					sBody = sBody.substr(sBody.indexOf("{")+1);
					if(sBody.indexOf("return")!=-1)
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("return"));
					}
					else
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("}"));
					}
					oEventDiv.setAttribute("body_dblclick", sBody);
					oEventDiv.ondblclick = function ()	{ if(this.parentNode.getAttribute('state')=='enabled') eval(this.getAttribute("body_dblclick")); };
				}
				if(oEventDiv.getAttribute("onmousedown")!=null && oEventDiv.getAttribute("body_mousedown")==null)
				{
					var sBody = oEventDiv.getAttribute("onmousedown").toString();
					sBody = sBody.substr(sBody.indexOf("{")+1);
					if(sBody.indexOf("return")!=-1)
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("return"));
					}
					else
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("}"));
					}
					oEventDiv.setAttribute("body_mousedown", sBody);
					oEventDiv.onmousedown = function ()	{ if(this.parentNode.getAttribute('state')=='enabled') eval(this.getAttribute("body_mousedown")); };
				}
				if(oEventDiv.getAttribute("onmouseup")!=null && oEventDiv.getAttribute("body_mouseup")==null)
				{
					var sBody = oEventDiv.getAttribute("onmouseup").toString();
					sBody = sBody.substr(sBody.indexOf("{")+1);
					if(sBody.indexOf("return")!=-1)
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("return"));
					}
					else
					{
						sBody = sBody.substring(0, sBody.lastIndexOf("}"));
					}
					oEventDiv.setAttribute("body_mouseup", sBody);
					oEventDiv.onmouseup = function ()	{ if(this.parentNode.getAttribute('state')=='enabled') eval(this.getAttribute("body_mouseup")); };
				}
				if(bNaviButton)
				{
					AddEvtHandler("EVENT_SLIDE_OPENED", "nav_000_button", argobj.pid);
					AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_000_button", argobj.pid);
					AddEvtHandler("EVENT_NAVIGATION", "nav_000_button", argobj.pid);
				}
				return true;
			}

function HandleEvt(oEvtHandler, oArg)
			{
				var argobj = { pid: oEvtHandler.sTargetId };
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
				var bNavLock = (oParams.selectSingleNode("nav_lock").text=="yes");
				switch (oEvtHandler.sEvt)
				{
					case "EVENT_SLIDE_OPENED":
					{
						if(bNavLock) CallMethod("nav_000_button", "DisableAccess", argobj);
						break;
					}
					case "EVENT_SLIDE_COMPLETE":
					{
						if(bNavLock) CallMethod("nav_000_button","EnableAccess",argobj);
						break;
					}
					case "EVENT_NAVIGATION":
					{
						if(oArg==null) return false;
						if(oArg.sTargets=="all" || oArg.sTargets=="buttons")
						{
							CallMethod("nav_000_button", (oArg.sAction=="on") ? "EnableAccess" : "DisableAccess", argobj);
						}
						break;
					}
				}
				return true;
			}

function DisableAccess(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oEnabled = document.getElementById(argobj.pid + "_btn_enabled");
				var oDisabled = document.getElementById(argobj.pid + "_btn_disabled");
				if(oEnabled==null || oDisabled==null) return false;
				oEnabled.style.display = "none";
				oDisabled.style.display = g_isMSIE ? "inline" : "block";
				oCurObj.setAttribute("state","disabled");
				return true;
			}

function EnableAccess(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oEnabled = document.getElementById(argobj.pid + "_btn_enabled");
				var oDisabled = document.getElementById(argobj.pid + "_btn_disabled");
				if(oEnabled==null || oDisabled==null) return false;
				oDisabled.style.display = "none";
				oEnabled.style.display = g_isMSIE ? "inline" : "block";
				oCurObj.setAttribute("state","enabled");
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "disabled":    g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";    break;
					default: return true;
				}
				return true;
			}

function Over(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
				if(oParams==null) return false;
				var oEnabled = document.getElementById(argobj.pid+"_btn_enabled");
				var oHighlighted = document.getElementById(argobj.pid+"_btn_highlighted");
				if(oEnabled==null || oHighlighted==null) return false;
				oEnabled.src = oHighlighted.src;
				if(oParams.selectSingleNode("sound.enabled").text=="yes") PlayObjectSound({ pid: argobj.pid, snd: "over" });
				return true;
			}

function Out(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oEnabled = document.getElementById(argobj.pid+"_btn_enabled");
				var oBackup = document.getElementById(argobj.pid+"_btn_normal");
				if(oEnabled==null || oBackup==null) return false;
				oEnabled.src = oBackup.src;
				return true;
			}

function Click(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.getAttribute("state")=="disabled") return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
				if(oParams==null) return false;
				var bUseMethod = (oParams.selectSingleNode("use_object").text=="yes");
				if(bUseMethod)
				{
					var sObjectId = oParams.selectSingleNode("target_object").text;
					if(sObjectId==null || sObjectId=="") return false;
					var oObjectNode = g_oSlides.selectSingleNode("//object[@id='" + sObjectId + "']");
					if(oObjectNode==null) return false;
					var oObject = document.getElementById(sObjectId);
					if(oObject==null) return false;
					CallMethod(oObjectNode.getAttribute("type"), (oObject.getAttribute("open")=="1") ? "Close" : "Open", { pid: sObjectId });
				}
				if(oParams.selectSingleNode("sound.enabled").text=="yes") PlayObjectSound({ pid: argobj.pid, snd: "click" });
				return true;
			}

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				oCurObj.setAttribute("num","");
				AddEvtHandler("EVENT_SLIDE_OPENED", "nav_002_position", argobj.pid);
				return true;
			}

function HandleEvt(oEvtHandler)
			{
				if(oEvtHandler.sTargetId==null) return false;
				var oCurObj = document.getElementById(oEvtHandler.sTargetId);
				if(oCurObj==null) return false;
				switch (oEvtHandler.sEvt)
				{
					case "EVENT_SLIDE_OPENED":
					{
						// Detecting structure object
						var sCurrentSlideId = g_oSlide.getAttribute("id");
						var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
						var bStructure = (oStructure!=null);
						var iCurrent = 0;
						var sCurrent = "";
						if(bStructure)
						{
							var sStructureId = oStructure.getAttribute("id");
							var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId +  "']");
							if(oStructureParams==null) return false;

							var aChapters = oStructureParams.selectNodes("chapter_list/item");
							var aSlideStrip = new Array();
							var aChapterSlides;
							var iIndex = 1;
							for(var i=0; i<aChapters.length; i++)
							{
								sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
								if(sId!="")
								{
									aSlideStrip.push(sId);
									if(sId==sCurrentSlideId) iCurrent = iIndex;
									iIndex++;
								}
								aChapterSlides = aChapters[i].selectNodes("slide_list/item");
								for(var j=0; j<aChapterSlides.length; j++)
								{
									sId = aChapterSlides[j]. selectSingleNode("slide_id").text;
									if(sId!="")
									{
										aSlideStrip.push(sId);
										if(sId==sCurrentSlideId) iCurrent = iIndex;
										iIndex++;
									}
								}
							}
							sTotal = aSlideStrip.length.toString();
							sCurrent = iCurrent.toString();
							oCurObj.setAttribute("num", sCurrent);
							document.getElementById(oEvtHandler.sTargetId + "_position_total").innerHTML = sTotal;
							document.getElementById(oEvtHandler.sTargetId + "_position_current").innerHTML = sCurrent;
						}
						else
						{
							var aSlides = g_oSlides.selectNodes("slide");
							sTotal = aSlides.length.toString();
							document.getElementById(oEvtHandler.sTargetId + "_position_total").innerHTML = sTotal;
							for(var i=0; i<aSlides.length; i++)
							{
								iCurrent++;
								var oSlide = aSlides[i];
								if(oSlide.getAttribute("id")==sCurrentSlideId) break;
							}
							sCurrent = iCurrent.toString();
							oCurObj.setAttribute("num", sCurrent);
							document.getElementById(oEvtHandler.sTargetId + "_position_current").innerHTML = sCurrent;
						}
						break;
					}
				}
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "currentposition":        g_vPropertyValue = oCurObj.getAttribute("num");        break;
					default: return true;
				}
				return true;
			}

function Constructor(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;
            var bDisplayLetters = oParams.selectSingleNode("display_letters").text=="yes";

            var oObjWin = document.getElementById(argobj.pid+"_object_window_div");
            oObjWin.style.visibility = "hidden";
            oObjWin.style.display = g_isMSIE ? "inline" : "block";

            var iFullHeight = parseInt(oParams.selectSingleNode("tabheight").text,10);
            if(isNaN(iFullHeight)) iFullHeight = oObjWin.offsetHeight;
            var iHeaderHeight = document.getElementById(argobj.pid+"_header").offsetHeight;
            var iLettersHeight = document.getElementById(argobj.pid+"_letters_td").offsetHeight;
            var oResultDiv = document.getElementById(argobj.pid+"_results");
            var oTermsDiv = document.getElementById(argobj.pid+"_terms");

            var iNewHeight = bDisplayLetters ? (iFullHeight - iLettersHeight - iHeaderHeight - 36) : (iFullHeight - iHeaderHeight - 36);
            oTermsDiv.style.height = iNewHeight.toString() + "px";
            oResultDiv.style.height = iNewHeight.toString() + "px";

            oObjWin.style.visibility = "visible";
            oObjWin.style.display = "none";

            var bFirstRun = oCurObj.getAttribute("firstrun")==null ? true : false;
            var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
            var sNavLock = oParams.selectSingleNode("nav_lock").text=="yes" ? "1" : "0";
            if(bFirstRun)
            {
                var oRow = document.getElementById(argobj.pid+"_letters_row");
                if(bDisplayLetters)
                {
                    var oTemplateCell = document.getElementById(argobj.pid+"_letters_all");
                    var oCell;
                    var aItems = oParams.selectNodes("item_list/item/e_item");
                    var aLetters = new Array();
                    var sLetters = "";
                    var sLetter = "";
                    var sItem = "";
                    for(var i=0;i<aItems.length;i++)
                    {
                        sItem = trim(aItems[i].text, true, true, true, true);
                        sLetter = sItem.charAt(0);
                        sLetter = sLetter.toUpperCase();
                        if(sLetters.indexOf(sLetter)!=-1) continue;
                        sLetters += sLetter;
                        aLetters.push(sLetter);
                    }
                    var aSortedLetters = aLetters.sort();
                    for(var i=0;i<aSortedLetters.length; i++)
                    {
                        oCell = oTemplateCell.cloneNode(true);
                        oCell.innerHTML = aSortedLetters[i];
                        oRow.appendChild(oCell);
                    }
                }
                else
                {
                    oRow.style.display = "none";
                }

                var oDefText = oParams.selectSingleNode("e_default_text");
                if(oDefText!=null)
                {
                    oResultDiv.innerHTML = oDefText.text;
                }
                else
                {
                    oResultDiv.innerHTML = "&nbsp;";
                }
                oCurObj.setAttribute("navlock", sNavLock);
                oCurObj.setAttribute("firstrun", "1");
                oCurObj.setAttribute("state", "enabled");
                oCurObj.setAttribute("winstatus", "closed");
                oCurObj.setAttribute("helpstatus","0");
                if(document.getElementById(argobj.pid+"_header").getElementsByTagName("p").length!=0)
                {
                    //fixing Gecko-specific p rendering
                    var aTmp = document.getElementById(argobj.pid+"_header").getElementsByTagName("p");
                    for(var i=0;i<aTmp.length;i++)
                    {
                        aTmp[i].style.padding = "0px";
                        aTmp[i].style.margin = "0px";
                    }
                }
            }
            if(document.getElementById(argobj.pid+"_object_window_clone")==null)
            {
                var oObj = oObjWin.cloneNode(true);
                oObj.setAttribute("id", argobj.pid+"_object_window_clone");
                document.getElementById("boardFrame").appendChild(oObj);
            }
            else
            {
                var oObj = document.getElementById(argobj.pid+"_object_window_div");
            }

            if(bFirstRun)
            {
                AddEvtHandler("EVENT_SLIDE_OPENED", "nav_021_gloss", argobj.pid);
                AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_021_gloss", argobj.pid);
                AddEvtHandler("EVENT_NAVIGATION", "nav_021_gloss", argobj.pid);
            }
            return true;
        }

function HandleEvt(oEvtHandler, oArg)
        {
            var argobj = new Object;
            argobj.pid = oEvtHandler.sTargetId;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var sCurrentSlideId = g_oSlide.getAttribute("id");
            var sSavedSlideId = oCurObj.getAttribute("sid");
            if(sSavedSlideId==null) sSavedSlideId = "";
            switch (oEvtHandler.sEvt)
            {
                case "EVENT_SLIDE_OPENED":
                    if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
                    if(oCurObj.getAttribute("closed")=="0") CallMethod("nav_021_gloss","ToggleWindow",argobj);
                    if(oCurObj.getAttribute("navlock")=="1") CallMethod("nav_021_gloss","DisableAccess",argobj);
                    break;
                case "EVENT_SLIDE_COMPLETE":
                    if(oCurObj.getAttribute("state")=="disabled" && sSavedSlideId=="")
                    {
                        oCurObj.setAttribute("sid","");
                        CallMethod("nav_021_gloss","EnableAccess",argobj);
                    }
                    break;
                case "EVENT_NAVIGATION":
                    if(oArg==null) return false;
                    if(oArg.sTargets=="search" || oArg.sTargets=="all")
                    {
                        if(oArg.sAction=="on" && oCurObj.getAttribute("state")=="disabled")
                        {
                            oCurObj.setAttribute("sid","");
                            CallMethod("nav_021_gloss","EnableAccess",argobj);
                        }
                        if(oArg.sAction=="off" && oCurObj.getAttribute("state")=="enabled")
                        {
                            oCurObj.setAttribute("sid",sCurrentSlideId);
                            CallMethod("nav_021_gloss","DisableAccess",argobj);
                        }
                    }
                    break;
                default:
                    break;
            }
            return true;
        }

function ToggleWindow(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(document.getElementById(argobj.pid+"_object_window_clone")!=null)
            {
                var oObj = document.getElementById(argobj.pid+"_object_window_clone");
                if(oObj.style.display != "none")
                {
                    oObj.style.display = "none";
                    oCurObj.setAttribute("winstatus","closed");
                    document.getElementById(argobj.pid+"_button_img_enabled").src = document.getElementById(argobj.pid+"_img_enabled_0").src;
                }
                else
                {
                    oObj.style.display =  g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("winstatus","open");
                }
            }
            else
            {
                CallMethod("nav_021_gloss","Constructor",argobj);
                var oObj = document.getElementById(argobj.pid+"_object_window_clone");
                oObj.style.display =  g_isMSIE ? "inline" : "block";
                oCurObj.setAttribute("winstatus","open");
            }
            PlayObjectSound({ pid: argobj.pid, snd: "click" });
            return true;
        }

function DisableAccess(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
            if(!bEnabled) return false;
            if(document.getElementById(argobj.pid+"_object_window_clone")!=null)
            {
                if(document.getElementById(argobj.pid+"_object_window_clone").style.display != "none") document.getElementById(argobj.pid+"_object_window_clone").style.display = "none";
            }
            document.getElementById(argobj.pid+"_button_enabled").style.display = "none";
            document.getElementById(argobj.pid+"_button_disabled").style.display = g_isMSIE ? "inline" : "block";
            oCurObj.setAttribute("state","disabled");
            return true;
        }

function EnableAccess(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
            if(bEnabled) return false;
            document.getElementById(argobj.pid+"_button_disabled").style.display = "none";
            document.getElementById(argobj.pid+"_button_enabled").style.display = g_isMSIE ? "inline" : "block";
            document.getElementById(argobj.pid+"_button_img_enabled").src=document.getElementById(argobj.pid+"_img_enabled_0").src;
            oCurObj.setAttribute("state","enabled");
            return true;
        }

function Click(argobj)
        {
            if(argobj.pid==null || argobj.elem==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var aoItems = oParams.selectNodes("item_list/item");
            if(aoItems.length==0) return false;
            var oElem = argobj.elem;
            var sState = oElem.getAttribute("curstate");
            if(sState=="sel") return false;

            // deselect any other
            var oArgs = new Object;
            oArgs.pid = argobj.pid;
            oArgs.target = "items";
            CallMethod("nav_021_gloss","Reset",oArgs);

            oElem.setAttribute("curstate", "sel");

            oElem.style.fontFamily = oParams.selectSingleNode("selected_font").text;
            oElem.style.fontSize = oParams.selectSingleNode("selected_font_size").text + "px";
            oElem.style.fontWeight = oParams.selectSingleNode("selected_font_weight").text;
            oElem.style.fontStyle = oParams.selectSingleNode("selected_font_style").text;
            oElem.style.textDecoration = oParams.selectSingleNode("selected_font_underline").text;
            oElem.style.color = oParams.selectSingleNode("selected_font_color").text;
            oElem.style.backgroundColor = oParams.selectSingleNode("selected_bg_color").text;
            oElem.style.borderColor = oParams.selectSingleNode("selected_border_color").text;
            oElem.style.borderStyle = oParams.selectSingleNode("selected_border_style").text;
            oElem.style.borderWidth = oParams.selectSingleNode("selected_border_width").text + "px";
            var sBGImage = oParams.selectSingleNode("selected_bg_img").text;
            if(sBGImage!="")    oElem.style.backgroundImage = "url(" + sBGImage + ")";
            oElem.style.cursor = "default";

            var sText = trim(oElem.innerHTML, true, true, true, true);
            var oItem = oParams.selectSingleNode("item_list/item[e_item/text()='" + sText + "']");

            if(oItem==null)
            {
                sText = oParams.selectSingleNode("e_default_text").text;
            }
            else
            {
                sText = oItem.selectSingleNode("e_text").text;
            }

            var oObj = document.getElementById(argobj.pid+"_object_window_clone");
            aDivs = oObj.getElementsByTagName("div");
            var oDiv;
            for(var i=aDivs.length-1; i>=0; i--)
            {
                if(aDivs[i].getAttribute("cl_id")=="results")
                {
                    aDivs[i].innerHTML = sText;
                    break;
                }
            }
            PlayObjectSound({ pid: argobj.pid, snd: "click" });
            return true;
        }

function Filter(argobj)
        {
            if(argobj.pid==null || argobj.elem==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var oElem = argobj.elem;
            var sState = oElem.getAttribute("curstate");
            if(sState=="sel") return false;

            // deselect any other
            var oArgs = new Object;
            oArgs.pid = argobj.pid;
            oArgs.target = "items_letters";
            CallMethod("nav_021_gloss","Reset",oArgs);

            oElem.setAttribute("curstate", "sel");

            oElem.style.fontFamily = oParams.selectSingleNode("selected_font").text;
            oElem.style.fontSize = oParams.selectSingleNode("selected_font_size").text + "px";
            oElem.style.fontWeight = oParams.selectSingleNode("selected_font_weight").text;
            oElem.style.fontStyle = oParams.selectSingleNode("selected_font_style").text;
            oElem.style.textDecoration = oParams.selectSingleNode("selected_font_underline").text;
            oElem.style.color = oParams.selectSingleNode("selected_font_color").text;
            oElem.style.backgroundColor = oParams.selectSingleNode("selected_bg_color").text;
            oElem.style.borderColor = oParams.selectSingleNode("selected_border_color").text;
            oElem.style.borderStyle = oParams.selectSingleNode("selected_border_style").text;
            oElem.style.borderWidth = oParams.selectSingleNode("selected_border_width").text + "px";
            var sBGImage = oParams.selectSingleNode("selected_bg_img").text;
            if(sBGImage!="")    oElem.style.backgroundImage = "url(" + sBGImage + ")";
            oElem.style.cursor = "default";

            var sLetter = trim(oElem.innerHTML,true, true, true, true);

            var oObj = document.getElementById(argobj.pid+"_object_window_clone");
            var aDivs = oObj.getElementsByTagName("div");
            var oResDiv;
            var oTermsDiv;
            for(var i=0; i<aDivs.length; i++)
            {
                if(aDivs[i].getAttribute("cl_id")=="results")
                {
                    oResDiv = aDivs[i];
                    continue;
                }
                if(aDivs[i].getAttribute("cl_id")=="terms")
                {
                    oTermsDiv = aDivs[i];
                    continue;
                }
            }

            aDivs = oTermsDiv.getElementsByTagName("div");
            var sStr = "";
            var oFirstDiv;
            var bFirstItem = false;
            for(var i=0; i<aDivs.length; i++)
            {
                if(sLetter=="*" || sLetter=="")
                {
                    aDivs[i].style.display = g_isMSIE ? "inline" : "block";
                    if(!bFirstItem)
                    {
                        var oArgs = new Object;
                        oArgs.pid = argobj.pid;
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","");
                        CallMethod("nav_021_gloss","Click",oArgs);
                        bFirstItem = true;
                    }
                }
                else
                {
                    sStr = trim(aDivs[i].innerHTML,true,true,true,true);
                    sStr = sStr.charAt(0);
                    sStr = sStr.toUpperCase();
                    if(sLetter==sStr)
                    {
                        aDivs[i].style.display = g_isMSIE ? "inline" : "block";
                        if(!bFirstItem)
                        {
                            var oArgs = new Object;
                            oArgs.pid = argobj.pid;
                            oArgs.elem = aDivs[i];
                            aDivs[i].setAttribute("curstate","");
                            CallMethod("nav_021_gloss","Click",oArgs);
                            bFirstItem = true;
                        }
                    }
                    else
                    {
                        aDivs[i].style.display = "none";
                    }
                }
            }
            return true;
            }

function DisplayItem(argobj)
        {
            if(argobj.pid==null || argobj.ecode==null || argobj.eitem==null) return false;
            if(argobj.ecode=="" && argobj.eitem=="") return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
            if(oParams==null) return false;

            var oObj = document.getElementById(argobj.pid + "_object_window_clone");
            if(oObj==null)
            {
                var oObjWin = document.getElementById(argobj.pid + "_object_window_div");
                var oObj = oObjWin.cloneNode(true);
                oObj.setAttribute("id", argobj.pid + "_object_window_clone");
                document.getElementById("boardFrame").appendChild(oObj);
            }

            // deselect any other
            var oArgs = { pid: argobj.pid, target: "items_letters" };
            CallMethod("nav_021_gloss","Reset",oArgs);

            if(oObj.style.display == "none") CallMethod("nav_021_gloss","ToggleWindow",oArgs);

            var aDivs = oObj.getElementsByTagName("div");
            var oResDiv;
            var oTermsDiv;
            for(var i=0; i<aDivs.length; i++)
            {
                if(aDivs[i].getAttribute("cl_id")=="results")
                {
                    oResDiv = aDivs[i];
                    oResDiv.innerHTML = oParams.selectSingleNode("not_found").text;
                    continue;
                }
                if(aDivs[i].getAttribute("cl_id")=="terms")
                {
                    oTermsDiv = aDivs[i];
                    continue;
                }
            }
            aDivs = oTermsDiv.getElementsByTagName("div");

            for(var i=0; i<aDivs.length; i++)
            {
                if(argobj.ecode!="")
                {
                    if(aDivs[i].getAttribute("cl_code")!=argobj.ecode)
                    {
                        aDivs[i].style.display = "none";
                    }
                    else
                    {
						aDivs[i].style.display = "";
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","");
                        CallMethod("nav_021_gloss","Click",oArgs);
                    }
                }
                else
                {
                    if(aDivs[i].innerHTML!=argobj.eitem)
                    {
                        aDivs[i].style.display = "none";
                    }
                    else
                    {
						aDivs[i].style.display = "";
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","");
                        CallMethod("nav_021_gloss","Click",oArgs);
                    }
                }
            }
            return true;
        }

function Highlight(argobj)
        {
            if(argobj.pid==null || argobj.elem==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var oElem = argobj.elem;
            var sState = oElem.getAttribute("curstate");
            if(sState=="sel") return false;
            var bHL = sState=="hl";
            oElem.setAttribute("curstate", bHL ? "" : "hl");

            var oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font");
            if(oNode!=null && oNode.text!=null) oElem.style.fontFamily = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_size");
            if(oNode!=null && oNode.text!=null) oElem.style.fontSize = oNode.text + "px";
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_weight");
            if(oNode!=null && oNode.text!=null) oElem.style.fontWeight = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_style");
            if(oNode!=null && oNode.text!=null) oElem.style.fontStyle = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_underline");
            if(oNode!=null && oNode.text!=null) oElem.style.textDecoration = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_color");
            if(oNode!=null && oNode.text!=null) oElem.style.color = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "bg_color");
            if(oNode!=null && oNode.text!=null) oElem.style.backgroundColor = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "border_color");
            if(oNode!=null && oNode.text!=null) oElem.style.borderColor = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "border_style");
            if(oNode!=null && oNode.text!=null) oElem.style.borderStyle = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "border_width");
            if(oNode!=null && oNode.text!=null) oElem.style.borderWidth = oNode.text + "px";
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "bg_img");
            if(oNode!=null && oNode.text!=null)
            {
                var sBGImage = oNode.text;
                if(sBGImage!="")    oElem.style.backgroundImage = "url(" + sBGImage + ")";
            }
            return true;
        }

function Reset(argobj)
        {
            if(argobj.pid==null || argobj.target==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var bItems = argobj.target.indexOf("items")!=-1;
            var bLetters = argobj.target.indexOf("letters")!=-1;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid+"']");
            if(oParams==null) return false;
            var bDisplayLetters = oParams.selectSingleNode("display_letters").text=="yes";

            var oObj = document.getElementById(argobj.pid + "_object_window_clone");

            var oArgs = { pid: argobj.pid };

            if(bItems)
            {
                var aDivs = oObj.getElementsByTagName("div");
                var oResDiv;
                var oTermsDiv;
                for(var i=0;i<aDivs.length;i++)
                {
                    if(aDivs[i].getAttribute("cl_id")=="results")
                    {
                        oResDiv = aDivs[i];
                        continue;
                    }
                    if(aDivs[i].getAttribute("cl_id")=="terms")
                    {
                        oTermsDiv = aDivs[i];
                        continue;
                    }
                }
                aDivs = oTermsDiv.getElementsByTagName("div");
                for(var i=0;i<aDivs.length;i++)
                {
                    if(aDivs[i].getAttribute("curstate")=="sel")
                    {
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","hl");
                        CallMethod("nav_021_gloss","Highlight",oArgs);
                    }
                }
            }

            if(bLetters && bDisplayLetters)
            {
                var aTRs = oObj.getElementsByTagName("tr");
                var oTR;
                for(var i=0;i<aTRs.length;i++)
                {
                    if(aTRs[i].getAttribute("cl_id")=="letters_row")
                    {
                        oTR = aTRs[i];
                        break;
                    }
                }
                var aTDs = oTR.getElementsByTagName("td");
                for(var i=0;i<aTDs.length;i++)
                {
                    if(aTDs[i].getAttribute("curstate")=="sel")
                    {
                        oArgs.elem = aTDs[i];
                        aTDs[i].setAttribute("curstate","hl");
                        CallMethod("nav_021_gloss","Highlight",oArgs);
                    }
                }
            }
            return true;
        }

function GetProperty(argobj)
        {
            if(argobj.pid==null || argobj.property==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            switch(argobj.property.toLowerCase())
            {
                case "disabled":    g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";    break;
                case "open":        g_vPropertyValue = oCurObj.getAttribute("closed")=="1" ? "0" : "1";    break;
                default: return true;
            }
            return true;
        }

function Over(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                if(oCurObj.getAttribute("winstatus")!="closed") return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                PlayObjectSound({ pid: argobj.pid, snd: "over" });
                return true;
            }

function Out(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                if(oCurObj.getAttribute("winstatus")!="closed") return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                return true;
            }

function Down(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                return true;
            }

function BtnClick(argobj)
            {
                if(argobj.pid==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                CallMethod("nav_021_gloss", "ToggleWindow", { pid: argobj.pid });
                return true;
            }

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				oCurObj.setAttribute("highlighted","0");
				oCurObj.setAttribute("active","0");
				if(!g_isMSIE)
				{
					var oHighlighted = document.getElementById(argobj.pid + "_HIGHLIGHT");
					oHighlighted.style.width = oHighlighted.getAttribute("ww3c");
					oHighlighted.style.height = oHighlighted.getAttribute("hw3c");
				}
				if(oCurObj.getAttribute("onclick")==null) oCurObj.style.cursor = "default";
				return true;
			}

function HighlightOn(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.style.display=="none" || oCurObj.getAttribute("active")=="1" || oCurObj.getAttribute("highlighted")!="0") return false;
				oCurObj.setAttribute("active","1");
				var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
				if(oParams==null) return false;
				var oEmpty = document.getElementById(argobj.pid + "_EMPTYAREA");
				var oHighlighted = document.getElementById(argobj.pid + "_HIGHLIGHT");
				if(oEmpty==null || oHighlighted==null) return false;
				if(oCurObj.getAttribute("highlighted")!="1")
				{
					//oEmpty.style.display = "none";
					oHighlighted.style.display = "";
					if(oParams.selectSingleNode("blink").text=="yes")
					{
						var sDelay = oParams.selectSingleNode("timer").text;
						var nDelay = parseFloat(sDelay);
						if(isNaN(nDelay)) nDelay = 1;
						TransitionBlink({ targetid: argobj.pid + "_HIGHLIGHT", delay: nDelay });
					}
					oCurObj.setAttribute("highlighted","1");
				}
				return true;
			}

function HighlightOff(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.style.display=="none") return false;
				oCurObj.setAttribute("active","0");
				var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
				if(oParams==null) return false;
				var oEmpty = document.getElementById(argobj.pid + "_EMPTYAREA");
				var oHighlighted = document.getElementById(argobj.pid + "_HIGHLIGHT");
				if(oEmpty==null || oHighlighted==null) return false;
				if(oEmpty.style.display=="none")
				{
					oHighlighted.style.display = "none";
					//oEmpty.style.display = "";
					oCurObj.setAttribute("highlighted","0");
					if(oParams.selectSingleNode("blink").text=="yes") ClearTransitionBlink(argobj.pid);
				}
				return true;
			}

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
				if(oParams==null) return false;
				oCurObj.setAttribute("ready", "0");
				var bTW = (oParams.selectSingleNode("typewriter").text=="yes");
				var oTxt = document.getElementById(argobj.pid + "_txt");
				var oTxtShadow = document.getElementById(argobj.pid + "_txt_shadow");
				if(oTxt==null) return false;
				var sStr = oParams.selectSingleNode("box_text").text;
				var sRes = ReplaceMacrosInStr(sStr);
				oTxt.innerHTML = sRes;
				var aPs = oTxt.getElementsByTagName("p");
				if(aPs[0]!=null) aPs[aPs.length-1].style.marginBottom = "0px";
				if(oTxtShadow!=null)
				{
					oTxtShadow.innerHTML = sRes;
					aPs = oTxtShadow.getElementsByTagName("p");
					if(aPs[0]!=null) aPs[aPs.length-1].style.marginBottom = "0px";
				}
				if(!bTW || argobj.refresh)
				{
					oCurObj.setAttribute("ready", "1");
					return false;
				}
				var oSpeed = oParams.selectSingleNode("typewriter_speed");
				if(oSpeed==null)
				{
					oCurObj.setAttribute("ready", "1");
					return false;
				}
				var iSpeed = parseInt(oSpeed.text, 10);
				if(isNaN(iSpeed)) iSpeed = 8;
				iSpeed = Math.round(1000/iSpeed);
				oCurObj.setAttribute("speed", iSpeed.toString());
				var sTxtOrig = Trim(oTxt.innerHTML);
				var aChunks0 = sTxtOrig.split(">");
				var aChunks1;
				var bEntity = false;
				var sChar;
				var iCnt = 0;
				var sNewStr = "";
				for(var i=0; i<aChunks0.length; i++)
				{
					aChunks1 = aChunks0[i].split("<");
					for(var j=0; j<aChunks1.length; j+=2)
					{
						bEntity = false;
						sNewStr = "";
						for(var k=0; k<aChunks1[j].length; k++)
						{
							sChar = aChunks1[j].charAt(k);
							if(bEntity)
							{
								sNewStr += sChar;
								if(sChar==";") bEntity = false;
								continue;
							}
							else
							{
								if(sChar=="&")
								{
									bSemicolon = false;
									for(var m=k+1; m<k+10; m++)
									{
										if(aChunks1[j].charAt(m)==null) break;
										if(aChunks1[j].charAt(m)==";")
										{
											bSemicolon = true;
											break;
										}
									}
									if(bSemicolon)
									{
										sNewStr += sChar;
										bEntity = true;
									}
									else
									{
										sNewStr += "<span id='" + argobj.pid + "_i" + iCnt.toString() + "' style='visibility:hidden'>" + sChar + "</span>";
										iCnt++;
									}
									continue;
								}
								sNewStr += "<span id='" + argobj.pid + "_i" + iCnt.toString() + "' style='visibility:hidden'>" + sChar + "</span>";
								iCnt++;
							}
						}
						aChunks1[j] = sNewStr;
					}
					aChunks0[i] = aChunks1.join("<");
				}
				oTxt.innerHTML = aChunks0.join(">");
				oCurObj.setAttribute("cid", "0");
				oCurObj.setAttribute("lid", iCnt.toString());
				var oObject = g_oSlides.selectSingleNode("//object[@id='" + argobj.pid + "']");
				if(oObject==null)
				{
					oCurObj.setAttribute("ready", "1");
					return false;
				}
				var sDisplay = oObject.getAttribute("display");
				var sBegin = oObject.getAttribute("begin");
				var sTranIn = oObject.getAttribute("tranin"); 
				if(sDisplay=="none" || (sBegin!=null && sBegin!="0") || sTranIn!=null)
				{
					AddEvtHandler("EVENT_OBJECT_ONLOAD", "001_textbox", argobj.pid);
				}
				else
				{
					CallMethod("001_textbox", "TypeChar", argobj);
				}
				return true;
			}

function HandleEvt(oEvtHandler, oArg)
			{
				var argobj = { pid: oEvtHandler.sTargetId };
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				switch (oEvtHandler.sEvt)
				{
					case "EVENT_OBJECT_ONLOAD":
					{
						var oObject = g_oSlides.selectSingleNode("//object[@id='" + argobj.pid + "']");
						if(oObject==null) return false;
						var sTranIn = oObject.getAttribute("tranin");
						if(sTranIn==null)
						{
							CallMethod("001_textbox", "TypeChar", argobj);
						}
						else
						{
							var sDurIn = oObject.getAttribute("durin");
							if(sDurIn==null)
							{
								CallMethod("001_textbox", "TypeChar", argobj);
							}
							else
							{
								var iDurIn = parseInt(sDurIn, 10);
								if(isNaN(iDurIn))
								{
									CallMethod("001_textbox", "TypeChar", argobj);
								}
								else
								{
									CreateTimeActionMethod("001_textbox", "TypeChar", null, iDurIn*1000, argobj);
								}
							}
						}
						break;
					}
				}
				return true;
			}

function TypeChar(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var iCnt = parseInt(oCurObj.getAttribute("cid") ,10);
				if(isNaN(iCnt)) return false;
				var oSpan = document.getElementById(argobj.pid + "_i" + iCnt.toString());
				if(oSpan!=null) oSpan.style.visibility = "visible";
				iCnt++;
				if(document.getElementById(argobj.pid + "_i" + iCnt.toString())!=null)
				{
					var iDelay = parseInt(oCurObj.getAttribute("speed"), 10);
					oCurObj.setAttribute("cid", iCnt.toString());
					CreateTimeActionMethod("001_textbox", "TypeChar", null, iDelay, argobj);
				}
				else
				{
					oCurObj.setAttribute("ready", "1");
					FireEvent(argobj.pid,"onTypeWriterEnd");
				}
				return true;
			}

function CloseObject(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				CloseObject(argobj.pid);
				FireEvent(argobj.pid,"onCloseObject");
				return true;
			}

function RefreshText(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.getAttribute("ready")=="0") return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
				if(oParams==null) return false;
				argobj.refresh = true;
				CallMethod("001_textbox", "Constructor", argobj);
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				FireEvent(argobj.pid, "onRefresh");
				return true;
			}

function AddText(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.getAttribute("ready")=="0") return false;
				var oTxt = document.getElementById(argobj.pid + "_txt");
				var oTxtShadow = document.getElementById(argobj.pid + "_txt_shadow");
				if(oTxt==null) return false;
				var sText = oTxt.innerHTML;
				var sValue = argobj.value;
				sValue = ReplaceMacrosInStr(sValue);

				if(argobj.how=="p")
				{
					var aPs = oTxt.getElementsByTagName("p");
					if(aPs[0]==null) argobj.how = "break";
				}
				var iLeft = 0;
				var iRight = 0;
				switch(argobj.how)
				{
					case "break":
					case "space":
					{
						var sDivider = (argobj.how=="space") ? " " : "<br/>";
						var sBuffer = sText;
						iRight = sBuffer.length;
						if(argobj.where=="start")
						{
							while(sBuffer.charAt(0)=="<")
							{
								iLeft += sBuffer.indexOf(">") + 1;
								sBuffer = sBuffer.substr(iLeft);
								if(sBuffer.length==0) return false;
							}
							var sLeft = sText.substring(0, iLeft);
							var sRight = sText.substr(iLeft);
							oTxt.innerHTML = sLeft + sValue + sDivider + sRight;
						}
						else
						{
							while(sBuffer.charAt(sBuffer.length-1)==">")
							{
								iRight = sBuffer.lastIndexOf("<");
								sBuffer = sBuffer.substring(0, iRight);
								if(sBuffer.length==0) return false;
							}
							var sLeft = sText.substring(0, iRight);
							var sRight = sText.substr(iRight);
							oTxt.innerHTML = sLeft + sDivider + sValue + sRight;
						}
						break;
					}
					case "p":
					{
						var oP = (argobj.where=="end") ? aPs[aPs.length-1] : aPs[0];
						var oNewP = oP.cloneNode(true); 
						if(argobj.where=="end")
						{
							oTxt.appendChild(oNewP);
							oP.style.marginBottom = "";
						}
						else
						{
							oTxt.insertBefore(oNewP, oP);
							oNewP.style.marginBottom = "";
						}
						var oChild = oNewP.firstChild; 
						if(oChild.nodeType!=3)
						{
							var iCnt = 0;
							do
							{
								oChild = oChild.firstChild;
								iCnt++;
								if(iCnt>20) return false;
							}
							while(oChild!=null && oChild.nodeType!=3)
						}
						var oParent = oChild.parentNode;
						while(oParent.nextSibling!=null)
						{
							oParent.parentNode.removeChild(oParent.nextSibling);
						}
						oParent.innerHTML = sValue;
						break;
					}
				}
				if(oTxtShadow!=null) oTxtShadow.innerHTML = oTxt.innerHTML;
				FireEvent(argobj.pid,"onAddText");
				return true;
			}
